var ActionItem=function(a){this.m_parent=a;this.m_progress=this.m_parent.querySelector(".action-item__progress");this.hideProgress();this.setProgress(0);this.m_icon=this.m_parent.querySelector(".action-item__icon");this.m_caption=this.m_parent.querySelector(".action-item__caption");this.addListeners()};ActionItem.prototype.addListeners=function(){var a=this;this.m_parent.addEventListener("click",function(){a.startAction()},!1)};
ActionItem.prototype.showProgress=function(){this.setProgress(0);this.m_progress.classList.add("loading");this.m_parent.classList.add("loading")};ActionItem.prototype.hideProgress=function(){this.m_progress.classList.remove("loading");this.m_parent.classList.remove("loading")};ActionItem.prototype.setProgress=function(a){0>=a?a=0:1<a&&(a=1);this.m_progress.style.width=100*a+"%"};ActionItem.prototype.startAction=function(){this.setProgress(0);this.showProgress()};
ActionItem.prototype.endClean=function(){this.hideProgress();this.setProgress(0)};ActionItem.prototype.endSuccess=function(){var a=this;this.endClean();this.m_parent.classList.add("success");setTimeout(function(){a.m_parent.classList.remove("success")},1500)};ActionItem.prototype.endError=function(){var a=this;this.endClean();this.m_parent.classList.add("error");setTimeout(function(){a.m_parent.classList.remove("error")},1500)};